styles:
:host *{
  margin:0;
  padding:0;
  border:0;
  vertical-align:baseline; }

:host{
  display:block;
  text-align:left;
  vertical-align:middle;
  margin:2px 0; }
  :host .off-screen{
    display:block;
    text-indent:-9999px;
    width:0;
    height:0; }
  :host .select-box{
    background-color:#ccc;
    background-size:contain;
    display:inline-block;
    margin:2px;
    width:60px;
    line-height:42px;
    text-align:center;
    cursor:pointer;
    vertical-align:top;
    height:40px;
    border:1px solid #EFEFEF; }
    :host .select-box:hover, :host .select-box:focus{
      border:1px solid #888; }
    :host .select-box div{
      background-repeat:repeat;
      background-color:transparent;
      background-position:0 0;
      border:1px solid transparent;
      height:40px;
      width:60px; }
    :host .select-box .highlight-icon{
      background-repeat:repeat;
      background-color:transparent;
      background-position:0 0;
      border:2px solid red;
      height:40px;
      width:60px; }
  :host .name{
    color:#444;
    font-size:0.8em;
    text-align:center;
    text-shadow:0 1px 0 #eee; }
  :host .i-select-footer{
    line-height:12px;
    text-align:center; }
    :host .i-select-footer .i-select-arrows{
      float:right; }
      :host .i-select-footer .i-select-arrows div{
        color:#444;
        cursor:pointer;
        display:inline-block;
        height:16px; }
        :host .i-select-footer .i-select-arrows div.disabled{
          color:#ddd;
          cursor:default;
          font-weight:bold; }
        :host .i-select-footer .i-select-arrows div .next,
        :host .i-select-footer .i-select-arrows div .prev,
        :host .i-select-footer .i-select-arrows div .last,
        :host .i-select-footer .i-select-arrows div .first{
          cursor:pointer;
          width:auto;
          display:inline-block;
          height:39px;
          text-indent:-99999px;
          -webkit-box-sizing:border-box;
                  box-sizing:border-box; }
        :host .i-select-footer .i-select-arrows div .i-select-pages{
          font-size:11px; }
        :host .i-select-footer .i-select-arrows div:hover, :host .i-select-footer .i-select-arrows div:focus{
          color:#777777; }
  :host .selected-icon{
    background-color:#ccc;
    background-size:contain;
    display:block;
    width:60px;
    height:100%;
    float:left;
    position:relative;
    text-align:center; }
    :host .selected-icon div{
      cursor:default;
      color:#404040;
      height:100%;
      width:60px; }
  :host .i-select{
    height:22px;
    background-color:#fff;
    border:1px solid #ededed;
    border-radius:0 4px 4px 0; }
    :host .i-select.focused{
      border:1px dotted #444; }
  :host .i-select-category select{
    border:0;
    line-height:20px;
    padding:3px;
    width:100%;
    height:40px;
    -ms-box-sizing:border-box;
    -o-box-sizing:border-box;
    -webkit-box-sizing:border-box;
    box-sizing:border-box;
    margin-bottom:5px;
    font-size:12px;
    display:block;
    border:1px solid #EDEDED;
    color:#404040;
    -ms-box-shadow:none;
    -o-box-shadow:none;
    -webkit-box-shadow:none;
    box-shadow:none;
    outline:none; }
    :host .i-select-category select option{
      padding:10px; }
  :host .i-select-button{
    cursor:pointer;
    display:block;
    float:left;
    height:100%;
    text-align:center;
    width:20px;
    background-color:#f4f4f4;
    border-left:1px solid #e1e1e1;
    border-radius:0 4px 4px 0; }
    :host .i-select-button:focus .select-icon-up-dir{
      border-top:5px solid grey; }
    :host .i-select-button:focus .select-icon-down-dir{
      border-top:5px solid grey; }
    :host .i-select-button:hover, :host .i-select-button:focus{
      background-color:#f1f1f1 div;
        background-color-color:#999999; }
    :host .i-select-button div{
      color:#aaaaaa;
      text-shadow:0px 1px 0px #FFF; }
    :host .i-select-button .select-icon-down-dir{
      border-left:5px solid transparent;
      border-right:5px solid transparent;
      border-top:5px solid black;
      float:left;
      height:0;
      margin:10px 5px;
      width:0; }
    :host .i-select-button .select-icon-up-dir{
      border-left:5px solid transparent;
      border-right:5px solid transparent;
      border-top:5px solid black;
      float:left;
      height:0;
      margin:15px 10px;
      width:0; }
  :host .select-icons-container{
    width:100%;
    -webkit-box-sizing:border-box;
    box-sizing:border-box;
    padding:5px;
    background-color:#fff;
    border:1px solid #d3d3d3; }
    :host .select-icons-container .loading{
      color:#eee;
      font-size:24px;
      margin:0 auto;
      padding:20px 0;
      text-align:center;
      width:100%; }
  :host .i-select-popup{
    position:absolute;
    z-index:10000;
    background-color:#fefefe;
    padding:5px;
    height:auto;
    width:210px;
    margin-top:-1px;
    -ms-box-shadow:0 1px 1px rgba(0, 0, 0, 0.04);
    -o-box-shadow:0 1px 1px rgba(0, 0, 0, 0.04);
    -webkit-box-shadow:0 1px 1px rgba(0, 0, 0, 0.04);
    box-shadow:0 1px 1px rgba(0, 0, 0, 0.04);
    border:1px solid #E5E5E5; }
  :host .i-select-search{
    position:relative; }
    :host .i-select-search input[type="text"]{
      text-transform:uppercase;
      border:1px solid #EDEDED;
      color:#404040;
      -ms-box-shadow:none;
      -o-box-shadow:none;
      -webkit-box-shadow:none;
      box-shadow:none;
      outline:none;
      width:100%;
      margin:3px 0;
      padding:3px;
      -webkit-box-sizing:border-box;
              box-sizing:border-box; }
    :host .i-select-search div{
      color:#ddd;
      position:absolute;
      right:10px;
      top:7px; }
  :host input::-webkit-input-placeholder{
    text-transform:uppercase;
    color:#ddd; }
  :host input:-moz-placeholder{
    text-transform:uppercase;
    color:#ddd; }
  :host input::-moz-placeholder{
    text-transform:uppercase;
    color:#ddd; }
  :host input:-ms-input-placeholder{
    text-transform:uppercase;
    color:#ddd !important; }
  :host .select-icon-spin3{
    position:absolute;
    top:0;
    left:0;
    width:16px !important;
    height:16px !important;
    display:inline-block;
    margin:0 !important;
    padding:3px !important; }
  :host .icons-select-error div:before{
    color:#444; }
  :host .select-icon-search{
    cursor:default; }
  :host .select-icon-cancel{
    cursor:pointer; }
  :host .select-icon-block{
    background-color:#fed0d0;
    background-size:contain; }
  :host .current-icon,
  :host .current-icon:hover,
  :host .current-icon:focus{
    border:1px solid #444; }
  :host .fa{
    padding:3px 6px;
    margin-top:5px; }
